import React from 'react';

interface TileProps {
    title: string;
    bodyText: string;
    backgroundImage: string;
}

const Tile: React.FC<TileProps> = ({title, bodyText, backgroundImage}) => {
  const tileStyle: React.CSSProperties = {
    backgroundImage: `url(${backgroundImage})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center'
  };

  return (
    <div className="d-flex align-items-center justify-content-center p-4 text-black" style={tileStyle}>
      <div className="text-center">
        <h1>{title}</h1>
        <p>{bodyText}</p>
      </div>
    </div>
  );
};

export default Tile;
